            И ТипЗначения(ТаблицыГруппДоступаПроверкаПрава.ТипТаблицы) = ТипЗначения(#Параметр(7))
        #Если "#Параметр(6)" = "ПравоДобавления" #Тогда
            И ТаблицыГруппДоступаПроверкаПрава.Добавление
