            #Иначе
                И НЕ
                (     Значение(ПланВидовХарактеристик.ВидыДоступа.ПравоЧтения)          <> ЕстьNULL(ЗависимостиПравДоступа.ПриПроверкеПрава#Параметр(2), Значение(ПланВидовХарактеристик.ВидыДоступа.ПравоИзменения))
                    И ТаблицыГруппДоступаПроверкаПраваОбъекта.ВидДоступаПравоДобавления <> ЕстьNULL(ЗависимостиПравДоступа.ПриПроверкеПрава#Параметр(2), Значение(ПланВидовХарактеристик.ВидыДоступа.ПравоИзменения))
                    И ТаблицыГруппДоступаПроверкаПраваОбъекта.ВидДоступаПравоИзменения  <> ЕстьNULL(ЗависимостиПравДоступа.ПриПроверкеПрава#Параметр(2), Значение(ПланВидовХарактеристик.ВидыДоступа.ПравоИзменения))
                )
            #КонецЕсли
            )
        )
        // Проверка ограничения права
        И
    #Если "#Параметр(3)" = "РасширенноеИЛИ" ИЛИ "#Параметр(3)" = "НеОграничиватьДоступКГруппам,РасширенноеИЛИ" #Тогда
        НЕ
         (
          Истина В // Не все проверки отключены.
          (
                Выбрать Первые 1 Истина // Выбор первого набора с не отключенной проверкой.
                ИЗ РегистрСведений.НаборыЗначенийДоступа КАК НомераНаборов
                ГДЕ
                      НомераНаборов.Объект = #Параметр(7)
                #Если "#Параметр(2)" = "Чтение" #Тогда
                    // Предполагается, что право Чтение может зависеть только от права Чтение
                    И НомераНаборов.Чтение = Истина
                #Иначе
                    И НЕ
                    (     НомераНаборов.ВидДоступаПравоЧтения     <> ЕстьNULL(ЗависимостиПравДоступа.ПриПроверкеПрава#Параметр(2), Значение(ПланВидовХарактеристик.ВидыДоступа.ПравоИзменения))
                        И НомераНаборов.ВидДоступаПравоДобавления <> ЕстьNULL(ЗависимостиПравДоступа.ПриПроверкеПрава#Параметр(2), Значение(ПланВидовХарактеристик.ВидыДоступа.ПравоИзменения))
                        И НомераНаборов.ВидДоступаПравоИзменения  <> ЕстьNULL(ЗависимостиПравДоступа.ПриПроверкеПрава#Параметр(2), Значение(ПланВидовХарактеристик.ВидыДоступа.ПравоИзменения))
                    )
                #КонецЕсли
                    И
                Истина В // Не все проверки текущего набора отключены.
                (
                    Выбрать Первые 1 Истина // Выбор первой не отключенной проверки текущего набора.
                    ИЗ РегистрСведений.НаборыЗначенийДоступа КАК НаборыЗначений
                    ГДЕ
                          НаборыЗначений.Объект      = #Параметр(7)
