                                        И ЗначенияГруппДоступа.ВидДоступа    = НаборыЗначений.ВидДоступа
                                )
                            )
                )
          ) И НЕ
    #Иначе
         (
    #КонецЕсли
          Истина В
          (
            Выбрать Первые 1 Истина // Выбор первого набора по которому доступ разрешен.
            ИЗ РегистрСведений.НаборыЗначенийДоступа КАК НомераНаборов
            ГДЕ
                  НомераНаборов.Объект = #Параметр(7)
            #Если "#Параметр(2)" = "Чтение" #Тогда
                // Предполагается, что право Чтение может зависеть только от права Чтение
                И НомераНаборов.Чтение = Истина
            #Иначе
                И НЕ
                (     НомераНаборов.ВидДоступаПравоЧтения     <> ЕстьNULL(ЗависимостиПравДоступа.ПриПроверкеПрава#Параметр(2), Значение(ПланВидовХарактеристик.ВидыДоступа.ПравоИзменения))
                    И НомераНаборов.ВидДоступаПравоДобавления <> ЕстьNULL(ЗависимостиПравДоступа.ПриПроверкеПрава#Параметр(2), Значение(ПланВидовХарактеристик.ВидыДоступа.ПравоИзменения))
                    И НомераНаборов.ВидДоступаПравоИзменения  <> ЕстьNULL(ЗависимостиПравДоступа.ПриПроверкеПрава#Параметр(2), Значение(ПланВидовХарактеристик.ВидыДоступа.ПравоИзменения))
                )
            #КонецЕсли
                И
